ruleToc.chapterList:
.index:has(.chapter)<js>
reg = /class="index"/i, rep = 'class="chapters"';
String(result.outerHtml()).replace(reg, rep);
</js>.chapters.0<js>
var chapters = [];
push = (text, href, title, vol) => chapters.push(`<a volume="${String(vol)}" title="${title}" href="${href}">${text}</a>`);
if (result.select('.volume').size() == 0) {
    let cps = result.select('.chapter');
    for (idx = 0; idx < cps.size(); idx++) {
        let cp = cps.get(idx).select('a');
        let text = cp.text();
        let href = cp.attr('href');
        let title = cp.attr('title');
        push(text, href, title, false);
    }
} else {
    while (result.select('.chapter').size() > 0) {
        let vol = result.select('.volume').get(0);
        let ch = result.select('.chapters');
        let cps = ch.get(2).select('.chapter');
        push(vol.text(), vol.attr('href'), '', true);
        for (idx = 0; idx < cps.size(); idx++) {
            let cp = cps.get(idx).select('a');
            let text = cp.text();
            let href = cp.attr('href');
            let title = cp.attr('title');
            push(text, href, title, false);
        }
        if (ch.size() > 3) {
            result = ch.get(1);
        } else break;
    }
}
chapters.join('');
</js>a

