bookSourceComment:
/*
	自动获取搜索地址

	需要使用源变量时，执行下面eval
	eval(String(source.bookSourceComment));

	*/
	//获取源变量
	_getData = () => {
		try {
			data = JSON.parse(source.getVariable());
		} catch (err) {
			data = {};
		}
		return data
 	}
 	//保存源变量 _setData(data)
 _setData = (obj) => {
 	if(typeof obj == "object"){
 		source.setVariable(JSON.stringify(obj));
 	}
 }
 _getData();

 //获取搜索地址
 _getUrl = () => {
 	//通过js文件获取所搜地址
 	let url = org.jsoup.Jsoup.parse(java.ajax(source.key+"skin/common/sedeso.js")).select("form").attr("action");
 	//js文件获取失败时用webView加载获取所搜地址
 	if(!String(url)){
 		url = org.jsoup.Jsoup.parse(java.webView(null, source.key, null)).select("form").attr("action");
 	}
 	return url
 }

